import React, { Component } from "react";
import Modal from "react-modal";
import { connect } from "react-redux";


const customStyles = {
    content : {
      top                   : '50%',
      left                  : '50%',
      right                 : '50%',
      bottom                : 'auto',
      marginRight           : '-50%',
      transform             : 'translate(-50%, -50%)'
    }
  };

  Modal.setAppElement('#root')


class CardView extends Component {

    state = {
        comments:[]
    }

  handleChangeText = event => this.setState({ comments: event.target.value });

    addComment = async Comment => {
        const { item, dispatch } = this.props;

        dispatch({
          type: "ADD_COMMENT_TO_CARD",
          payload: { comment:Comment, cardId:item.id }
        });
      };

    render() {
        const {comments} = this.state;
        const { show, onClose, item } = this.props;

        return (
                    <Modal
                        isOpen={show}
                        onRequestClose={onClose}
                        style={customStyles}
                        overlayClassName={"overlay"}
                    >
                        <div>
                        <button className="pull-right" onClick={onClose}>X</button>

                            <h1 style={{ flex: "1 90%" }}>{item.text}</h1>
                        </div>
                        <div>
                            {/* <h2>Description</h2> */}

                            <div className="form-group">
                                <label htmlFor="Comments">Comments</label>
                                <textarea className="form-control" id="Comments" rows="3" value={comments}
                                onChange={this.handleChangeText}></textarea>
                            </div>
                            <div
                                tabIndex="0"
                                className="Edit-Button"
                                style={{ backgroundColor: "#5aac44" }}
                                onClick={this.addComment(comments)}
                                >
                                Add Comment
                                </div>
                            <h2>Previous Comments</h2>

                        </div>
                    </Modal>
                );
    }
}
const mapStateToProps = (state, ownProps) => ({
    card: state.cardsById[ownProps.cardId]
  });

export default connect(mapStateToProps)(CardView);
