import React, { Component } from "react";
import Modal from "react-modal";

const customStyles = {
    content : {
      top                   : '50%',
      left                  : '50%',
      right                 : '50%',
      bottom                : 'auto',
      marginRight           : '-50%',
      transform             : 'translate(-50%, -50%)'
    }
  };

  Modal.setAppElement('#root')


class CardView extends Component {

    render() {
        const { show, onClose, item } = this.props;

        return (
                    <Modal
                        isOpen={show}
                        onRequestClose={onClose}
                        style={customStyles}
                        overlayClassName={"overlay"}
                    >
                        <div>
                            <h1 style={{ flex: "1 90%" }}>{item.text}</h1>
                            <button className="pull-right" onClick={onClose}>X</button>
                        </div>
                        <div>
                            <h2>Description</h2>

                            <div className="form-group">
                                <label htmlFor="Comments">Comments</label>
                                <textarea className="form-control" id="Comments" rows="3"></textarea>
                            </div>
                            <div
                                tabIndex="0"
                                className="Edit-Button"
                                style={{ backgroundColor: "#5aac44" }}
                                >
                                Add Comment
                                </div>
                            <h2>Status</h2>
                            <p>Comment</p>
                        </div>
                    </Modal>
                );
    }
}

export default CardView;
